import React from 'react';

import classes from './modal.module.css';

const modal = (props) => {
    return (
        <div 
            className = {classes.Modal}
            style     = {{
                            transform: props.show ? "translateX(0)" : "translateX(-100vh)",
                            opacity:   props.show ? "1" : "0"
                        }}
        >
            {props.children}
        </div>
    );
};

export default modal;
