import React, {Component} from 'react';

import Aux           from '../../hoc/Aux';
import Burger        from '../../components/Burger/Burger';
import BuildControls from '../../components/Burger/BuildControls/BuildControls';
import Modal         from '../../components/UI/Modal/Modal';
import OrderSummary  from '../../components/Burger/OrderSummary/OrderSummary';

const INGREDIENT_PRICES = {
    salad:  0.5,
    bacon:  0.4,
    cheese: 1.3,
    meat:   0.7    
};

class BurgerBuilder extends Component {
    constructor(props) {
        super(props);
        console.log("[BurgerBuilder.js] constructor");
    }

    state = {
        ingredients: {
            salad:  0,
            bacon:  0,
            cheese: 0,
            meat:   0
        },
        totalPrice:  4,
        purchasable: false,
        purchasing:  false
    }

    addIngredientHandler = (type) => {
        const updatedIngredientCount = this.state.ingredients[type] + 1;
        const updatedPrice           = this.state.totalPrice + INGREDIENT_PRICES[type];
        const updatedIngredients     = {...this.state.ingredients};
        updatedIngredients[type]     = updatedIngredientCount;
        this.setState({
            ingredients: updatedIngredients,
            totalPrice: updatedPrice
        });
        this.updatePurchaseState(updatedIngredients);
    }

    removeIngredientHandler = (type) => {
        const updatedIngredientCount = this.state.ingredients[type] - 1;    
        const updatedTotalPrice      = this.state.totalPrice - INGREDIENT_PRICES[type];
        const updatedIngredients     = {...this.state.ingredients};
        updatedIngredients[type]     = updatedIngredientCount;
        this.setState({
            ingredients: updatedIngredients,
            totalPrice: updatedTotalPrice
        });
        this.updatePurchaseState(updatedIngredients);
    }

    updatePurchaseState = (ingredients) => {
        const sum = Object.keys(ingredients)
            .map((ingredient) => ingredients[ingredient])
                .reduce((sum, el) => sum + el, 0);
        this.setState({purchasable: sum > 0});
    }

    updatePurchasingState = () => {
        this.setState({purchasing: true});
    }

    backdropWasClicked = () => {
        this.setState({purchasing: false});
    }

    modalCancelled = () => {
        this.setState({purchasing: false});
    }

    render() {
        const disabledInfo = {...this.state.ingredients};
        for(let key in disabledInfo) 
            disabledInfo[key] = disabledInfo[key] <= 0;

        return (
            <Aux>
                <Modal 
                    show            = {this.state.purchasing}
                    backdropClicked = {this.backdropWasClicked}
                    >
                    <OrderSummary 
                        ingredients = {this.state.ingredients}
                        modalCancel =Â {this.modalCancelled}
                    />
                </Modal>
                <Burger ingredients = {this.state.ingredients}/>
                <BuildControls 
                    addIngredient    = {this.addIngredientHandler}
                    removeIngredient = {this.removeIngredientHandler}
                    disabled         = {disabledInfo}
                    totalPrice       = {this.state.totalPrice}
                    purchasable      = {this.state.purchasable}
                    orderBtnClicked       = {this.updatePurchasingState}
                />
            </Aux>
        );
    }
}

export default BurgerBuilder;
